import React , {useEffect , useState} from 'react';
import Text from '../components/Text'
import io from "socket.io-client";
const socket = io();

function Message() {
    const [receivedMessages, setReceivedMessages] = useState(['text']);
    useEffect(()=> {
        console.log('effect')
        socket.on("new-messages", data => {	
			console.log('message')
			setReceivedMessages(prevState => [...prevState, data]);
		});
    },[])
    return (
        <div className='mt-5  border-2 max-w-xl mx-auto bg-white w-full rounded-xl shadow-md xl:max-w-2xl h-2/3 bg-gray-100'>
        <div className='text-center pl-2 pt-2 uppercase tracking-tight text-lg text-blue-700 font-semibold '>
            ROBOT MESSAGE
        </div>
        <div className="h-5/6 overflow-y-auto mb-10">
         {receivedMessages.map(message => { console.log(message)})}
            </div>



    </div>
    )
}

export default Message
