import React, { useState, useEffect } from "react";
import io from "socket.io-client";
const socket = io();

function Socket() {
  const [response, setResponse] = useState("");

  useEffect(() => {
    console.log("Emiting")
    socket.on("API", data => {
      setResponse(data);
      console.log(data)
    });
    socket.emit("message", "TEST");
  }, []);
  const sendMessage = ()=>{
    socket.emit("message" , "TEST")
    console.log('click')
    setResponse("")
  }
  return (
 <div>
      <p>
      It's <time dateTime={response}>{response}</time>
    </p>
    <button  className="border-2 bg-blue-400"onClick={sendMessage}>Click</button>
 </div>
  );
}

export default Socket;
