import React , {useEffect , useState} from 'react';
import Text from '../components/Text'
import io from "socket.io-client";
import Socket from '../pages/Socket';
const socket = io();


function Message({text}) {
    const [receivedMessagess, setReceivedMessagess] = useState(['']);
    useEffect(()=>{
        socket.on('test', data=>{
			console.log(receivedMessagess)
            setReceivedMessagess(prevState => [...prevState, data]);
		})
    },[])

    return (
        <div className='mt-5  border-2 max-w-xl mx-auto bg-white w-full rounded-xl shadow-md xl:max-w-2xl h-2/3 bg-gray-100'>
        <div className='text-center pl-2 pt-2 uppercase tracking-tight text-lg text-blue-700 font-semibold '>
            ROBOT MESSAGE
        </div>
        <div className="h-5/6 overflow-y-auto mb-10">
       {
       receivedMessagess.map((item,index)=>{
           <Text text={item} id={index} />
       })
       }

        </div>
      {/* /<Text text={message} />      */}

      <Socket/>
    </div>
    )
}

export default Message
