import React from 'react';
import Camera from '../components/Camera';
import Maps from '../components/Maps';
import Message from '../components/Message'
import DriverandGNSS from '../components/DriverandGNSS'
import socketIOClient from 'socket.io-client'
function Home() {

	componentDidMount = () => {
		this.response()
	  }

	  // เมื่อมีการส่งข้อมูลไปยัง server
	  send = (message) => {
		const { endpoint, input } = this.state
		const socket = socketIOClient(endpoint)
		socket.emit('sent-message', input)
		this.setState({ input: '' })
	  }

	  // รอรับข้อมูลเมื่อ server มีการ update
	  response = () => {
		const { endpoint, message } = this.state
		const temp = message
		const socket = socketIOClient(endpoint)
		socket.on('new-message', (messageNew) => {
		  temp.push(messageNew)
		  this.setState({ message: temp })
		})
	  }

	  changeInput = (e) => {
		this.setState({ input: e.target.value })
	  }
	return (
		<div className='2xl:px-20 grid grid-cols-2 mt-5 gap-1 lg:grid-cols-2'>
			<Camera />
			<Maps />
			<Message/>
			<DriverandGNSS />

		</div>
	);
}

export default Home;
