    return (
        <div className='mt-5  border-2 max-w-xl mx-auto bg-white w-full rounded-xl shadow-md xl:max-w-2xl h-2/3 bg-gray-100'>
        <div className='text-center pl-2 pt-2 uppercase tracking-tight text-lg text-blue-700 font-semibold '>
            ROBOT MESSAGE
        </div>
        <div className="h-5/6 overflow-y-auto mb-10">
       {
       text.map((item => { <Text text={item} /> })) 
       }
        </div>
      {/* /<Text text={message} />      */}


    </div>
    )
}

export default Message
