import React,{ useState , useEffect} from 'react'
import Text from '../components/Text'

function Message({message}) {
    const [receivedMessages, setReceivedMessages] = useState([]);
    useEffect(()=> {
        socket.on("new-message", message => {
            setReceivedMessages(prevState => [...prevState, message]);
          });
    }, [receivedMessages])
    return (
        <div className='mt-5  border-2 max-w-xl mx-auto bg-white w-full rounded-xl shadow-md xl:max-w-2xl h-2/3 bg-gray-100'>
        <div className='text-center pl-2 pt-2 uppercase tracking-tight text-lg text-blue-700 font-semibold '>
            ROBOT MESSAGE
        </div>
        <div className="h-5/6 overflow-y-auto mb-10">
        {console.log(receivedMessages)}
         {message.map(message => <Text text={receivedMessages} /> )}
            </div>



    </div>
    )
}

export default Message
